import math

def index_minimum_in_a_list(list):
	index_min = 0
	for i in range(len(list)):
		if list[i] < list[index_min]:
			index_min = i
	return index_min

def nth_greatest_value_of_multiplication_of_some_consecutive_digits(serie, nth, number_consecutive):
	digits = [int(i) for i in str(serie)]

	sum_selection = []
	digits_selection = []
	for x in range(nth):
		sum_selection.append(0)
		digits_selection.append([])

	# Pour chaque possibilitÃ©
	for i in range(len(serie) + 1 - number_consecutive):
		# Calculer la multiplication
		mul = 1
		for j in range(number_consecutive):
			mul *= int(digits[i + j])
		
		# Remplacer le minimum si mul > minimun
		k = index_minimum_in_a_list(sum_selection)
		if mul > sum_selection[k]:
			sum_selection[k] = mul
			digits_of_sum_selection = []
			for j in range(number_consecutive):
				digits_of_sum_selection.append(digits[i + j])
			digits_selection[k] = digits_of_sum_selection

	l = index_minimum_in_a_list(sum_selection)
	return [sum_selection[l], digits_selection[l]]

